package com.example.quanlynhatro.Activity.Nhatro;import androidx.appcompat.app.AppCompatActivity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.KeyboardShortcutGroup;import android.view.View;import android.view.inputmethod.InputMethodManager;import android.widget.AdapterView;import android.widget.EditText;import android.widget.GridView;import android.widget.ImageButton;import android.widget.TextView;import com.example.quanlynhatro.Activity.HomeMain;import com.example.quanlynhatro.Activity.Loaiphong.DanhsachLoaiphong;import com.example.quanlynhatro.Activity.MainActivity;import com.example.quanlynhatro.Adapter.CategoryAdapter;import com.example.quanlynhatro.ModelApp.CategoryHome;import com.example.quanlynhatro.R;import java.util.ArrayList;public class NhatroActivity extends AppCompatActivity {    private GridView gridViewNhatroInf;    private ArrayList<CategoryHome> arCategoryNhatro = new ArrayList<>();    private CategoryAdapter categoryAdapter;    private TextView txtTenTroInf;    private TextView txtSoLoaiInf;    private TextView txtSoPhongInf;    private TextView txtSokhachInf;    private int matro;    private ImageButton btnBackNhatro;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_nhatro);        matro = getIntent().getIntExtra("matro", -1);        Anhxa();        SetAdapterCategoryNhatro();        Loadata();        gridViewNhatroInf.setOnItemClickListener(new AdapterView.OnItemClickListener() {            @Override            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {                Intent intent = null;                switch (position){                    case 0:                        intent = new Intent(NhatroActivity.this, ThongtinnhatroInf.class);                        intent.putExtra("matro", matro);                        break;                    case 1:                        intent = new Intent(NhatroActivity.this, DanhsachLoaiphong  .class);                        intent.putExtra("matro", matro);                        break;                }                startActivity(intent);            }        });        btnBackNhatro.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                NhatroActivity.super.onBackPressed();            }        });    }    @Override    protected void onResume() {        Loadata();        super.onResume();    }    private void Loadata() {        txtTenTroInf.setText("Nhà Trọ: " + MainActivity.dataSQLite.getThongtintrotheomatro(matro).getmTentro() + " - Gồm:");        txtSoLoaiInf.setText("\t"+MainActivity.dataSQLite.getSoluongloaiphongcua1nhatro(matro)+" Loại phòng");        txtSoPhongInf.setText("\t"+MainActivity.dataSQLite.getSlPhongtrocua1nhatro(matro)+" Phòng trọ");        txtSokhachInf.setText("\t"+MainActivity.dataSQLite.getSlKhachcua1nhatro(matro)+" Khách trọ");    }    private void SetAdapterCategoryNhatro() {        arCategoryNhatro.add(new CategoryHome("Thông Tin", R.drawable.informationmotel));        arCategoryNhatro.add(new CategoryHome("Loại Phòng", R.drawable.typeroom128));        categoryAdapter = new CategoryAdapter(arCategoryNhatro, NhatroActivity.this, R.layout.line_category);        gridViewNhatroInf.setAdapter(categoryAdapter);        categoryAdapter.notifyDataSetChanged();    }    private void Anhxa() {        gridViewNhatroInf = (GridView) findViewById(R.id.gridViewNhatroInf);        txtTenTroInf = (TextView) findViewById(R.id.txtTenTroInf);        txtSoLoaiInf = (TextView) findViewById(R.id.txtSoLoaiInf);        txtSoPhongInf = (TextView) findViewById(R.id.txtSoPhongInf);        txtSokhachInf = (TextView) findViewById(R.id.txtSoKhachInf);        btnBackNhatro = (ImageButton) findViewById(R.id.btnBackNhatro);    }    private void CloseKeyboard(){        View view = this.getCurrentFocus();        if (view != null) {            InputMethodManager imm = (InputMethodManager) getSystemService(INPUT_METHOD_SERVICE);            imm.hideSoftInputFromWindow(view.getWindowToken(), 0);        }    }}